import React, { useContext } from 'react';
import { HotelContext } from '../../../context/HotelContext';

const AccommodationForm = () => {
    const { formData, setFormData } = useContext(HotelContext);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            accommodation: {
                ...formData.accommodation,
                [name]: value
            }
        });
    };

    return (
        <div className="p-6 bg-white rounded-lg shadow-md">
            <h2 className="text-2xl font-semibold mb-4">Accommodation</h2>
            <form className="w-full flex flex-wrap gap-4 justify-between mx-auto">
                <div className="w-full mb-4 border border-secondary rounded-2xl flex justify-between py-5 px-5">
                    <label className="block text-gray-700 font-medium mb-2">
                        How many Guest Rooms, Suites, Villas, Apartments, Penthouses your Hotel can accommodate?
                    </label>
                    <input
                        type="number"
                        name="guestRooms"
                        value={formData.accommodation.guestRooms}
                        onChange={handleInputChange}
                        min="0"
                        placeholder="00"
                        className="w-1/5 py-2 px-3 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    />
                    <input
                        type="number"
                        name="suites"
                        value={formData.accommodation.suites}
                        onChange={handleInputChange}
                        min="0"
                        placeholder="00"
                        className="w-1/5 py-2 px-3 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    />
                    <input
                        type="number"
                        name="villas"
                        value={formData.accommodation.villas}
                        onChange={handleInputChange}
                        min="0"
                        placeholder="00"
                        className="w-1/5 py-2 px-3 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    />
                    <input
                        type="number"
                        name="apartments"
                        value={formData.accommodation.apartments}
                        onChange={handleInputChange}
                        min="0"
                        placeholder="00"
                        className="w-1/5 py-2 px-3 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    />
                    <input
                        type="number"
                        name="penthouses"
                        value={formData.accommodation.penthouses}
                        onChange={handleInputChange}
                        min="0"
                        placeholder="00"
                        className="w-1/5 py-2 px-3 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                    />
                </div>
            </form>
        </div>
    );
};

export default AccommodationForm;
