import React, { useState } from 'react';
import ToggleSwitch from '../../shared/common/custom-toggle';
import AddFeatureModal from './add-feature-form';
import { FaPhoneAlt, FaConciergeBell, FaTshirt, FaSuitcaseRolling, FaGlobe, FaUtensils, FaBed, FaSnowflake, FaKitchen } from 'react-icons/fa';

const features = [
    { name: 'Calls (Local)', icon: <FaPhoneAlt /> },
    { name: 'Calls (Toll-free)', icon: <FaPhoneAlt /> },
    { name: 'Concierge Services', icon: <FaConciergeBell /> },
    { name: 'Laundry Service', icon: <FaTshirt /> },
    { name: 'Luggage Storage', icon: <FaSuitcaseRolling /> },
    { name: 'Internet Access', icon: <FaGlobe /> },
    { name: 'Room Service', icon: <FaBed /> },
    { name: 'Air conditioning', icon: <FaSnowflake /> },
    { name: 'Kitchen', icon: <FaUtensils /> },
];

const RoomFeatures = () => {
    const [enabledFeatures, setEnabledFeatures] = useState(
        features.reduce((acc, feature) => ({ ...acc, [feature.name]: true }), {})
    );

    const handleToggle = (featureName) => {
        setEnabledFeatures((prevState) => ({
            ...prevState,
            [featureName]: !prevState[featureName],
        }));
    };

    const handleAddFeature = (newFeature) => {
        setEnabledFeatures((prevState) => ({
            ...prevState,
            [newFeature.name]: true,
        }));
        features.push(newFeature);
    };

    const [isModalOpen, setIsModalOpen] = useState(false);

    return (
        <div className="flex">
            <div className="w-full">
                <div className="flex justify-between items-center w-full pb-5">
                    <h2 className="text-3xl font-bold mb-4">Room Features</h2>
                    <button onClick={() => setIsModalOpen(true)} className="text-primary font-medium ">Add Custom Feature</button>
                </div>
                {features.map((feature, index) => (
                    <div key={index} className="flex items-center py-1 justify-between lg:w-1/4 w-full mb-4">
                        <div className="flex items-center">
                            <span className="text-2xl mr-2">{feature.icon}</span>
                            <span className='text-base font-medium text-black'>{feature.name}</span>
                        </div>
                        <ToggleSwitch
                            isOn={enabledFeatures[feature.name]}
                            handleToggle={() => handleToggle(feature.name)}
                        />
                    </div>
                ))}
            </div>
            <AddFeatureModal isOpen={isModalOpen} onRequestClose={() => setIsModalOpen(false)} onSave={handleAddFeature} />
        </div>
    );
};

export default RoomFeatures;
