import React, { useState } from 'react';
import { MdKeyboardArrowDown, MdKeyboardArrowUp } from 'react-icons/md';

const offers = [
    {
        title: "Get 20% OFF for limited period Only.",
        description: "Enjoy a 20% discount on all bookings made within this limited period.",
    },
    {
        title: "Diwali party 50% OFF",
        from: "13th March, 2024",
        to: "16th March, 2024",
        description: `Join us at [Your Venue Name] for a spectacular Diwali celebration! Enjoy a grand buffet with a variety of traditional Indian dishes and sweets, complimentary welcome drinks, and special prices on beverages. Our live entertainment includes a DJ, cultural performances, and fun activities for all ages, all set against stunning festive décor with beautiful rangoli designs. Take advantage of our early bird and group booking discounts, and receive complimentary Diwali gift hampers for all guests. Celebrate with us on [Event Date] from [Start Time] to [End Time] at [Your Venue Name], [Venue Address]. Tickets are [Price] for adults, [Price] for children (6-12), and free for children under 6. Book now at [Website URL], call [Contact Number], or email [Email Address]. Light up your Diwali with us!`,
    },
];

const OffersAndPackages = () => {
    const [expandedIndex, setExpandedIndex] = useState(null);

    const toggleExpand = (index) => {
        setExpandedIndex(expandedIndex === index ? null : index);
    };

    return (
        <div className="bg-white  rounded-lg  mb-8 lg:w-3/4 w-full px-5 mx-auto py-10">
            <h2 className="text-3xl font-bold mb-4">Offers & Packages</h2>
            {offers.map((offer, index) => (
                <div key={index} className="mb-4">
                    <div
                        className="cursor-pointer bg-gray-100 p-4 rounded-lg flex justify-between items-center"
                        onClick={() => toggleExpand(index)}
                    >
                        <h3 className="text-xl font-semibold">{offer.title}</h3>
                        <span>{expandedIndex === index ? <MdKeyboardArrowUp className='text-3xl' /> : <MdKeyboardArrowDown className='text-3xl' />}</span>
                    </div>
                    {expandedIndex === index && (
                        <div className="mt-4 p-4 border rounded-lg bg-gray-50">
                            {offer.from && (
                                <div className="mb-2">
                                    <strong>From:</strong> {offer.from}
                                </div>
                            )}
                            {offer.to && (
                                <div className="mb-2">
                                    <strong>To:</strong> {offer.to}
                                </div>
                            )}
                            <p>{offer.description}</p>
                        </div>
                    )}
                </div>
            ))}
        </div>
    );
};

export default OffersAndPackages;
