// src/context/HotelContext.js
import React, { createContext, useState } from 'react';

export const HotelContext = createContext();

export const HotelProvider = ({ children }) => {
    const [formData, setFormData] = useState({
        basicDetails: {
            hotelName: '',
            hotelDescription: '',
            hotelWebsite: '',
            hotelRating: '',
            hotelChain: '',
            hotelBrand: '',
            selectedTags: {
                types: [],
                views: [],
                styles: {
                    traditional: [],
                    modern: [],
                    eclectic: [],
                    cultural: []
                }
            }
        },
        additionalDetails: {
            isSustainable: false,
            hotelBuiltYear: '',
            lastRenovatedYear: '',
            selectedEventTypes: [],
            awards: [],
            file: null 
        },
        locationDetails: {}
    });
console.log(formData);
    return (
        <HotelContext.Provider value={{ formData, setFormData }}>
            {children}
        </HotelContext.Provider>
    );
};
